import { Component } from 'react'
import io from "socket.io-client"

type props = {}
type state = {
  list: string
}

class App extends Component<props, state> {
  constructor(props: any){
    super(props)
    this.state = {
          selectedDevice: ""
    }
  }

  componentDidMount() {
    let socket = io(":7000")

    socket.emit("select_device", "12:E3:TC:UW", (data:any) => {
      console.log("selection data sent, data that got return is: " + data)
      this.setState({selectedDevice: data})
    })

    socket.on("devices_list", (data:any) => {
      console.log("devices list: " + data)
    })
  }

  render(){
    return (
        <div>
              Welcome to page!
              {this.state.list}
        </div>)
  }
}

export default App
