import { Component } from 'react'
import io from "socket.io-client"

type props = {}
type state = {
  list: string
}

class App extends Component<props, state> {
  constructor(props: any){
    super(props)
    this.state = {
          list: ""
    }
  }

  componentDidMount() {
    let socket = io(":7000/")
    let s2 = io(":7000/chat")

    socket.on('reply', (msg:string) => {
      console.log("got a message: " + msg)
    })

    s2.emit('msg', 'hi', () => {
      console.log('message sent')
    })
  }

  render(){
    return (
        <div>
              Welcome to page!
              {this.state.list}
        </div>)
  }
}

export default App
